    let gradient = CAGradientLayer()
    var count = 0
    
    
    // list of array holding 2 colors
    var gradientSet = [[CGColor]]()
    // current gradient index
    var currentGradient: Int = 0
    //index of emoji
    var number: Int = 0
    
    // colors to be added to the set
    let colorOne = #colorLiteral(red: 1, green: 1, blue: 1, alpha: 1).cgColor
    let colorTwo = #colorLiteral(red: 0.8078431487, green: 0.02745098062, blue: 0.3333333433, alpha: 1).cgColor
    let colorThree = #colorLiteral(red: 0.9607843161, green: 0.7058823705, blue: 0.200000003, alpha: 1).cgColor
    var didBegin: Bool = false
    var done = true //varialble makes it so finished is only called when finished
    
    
    // create outlet in the storyboard with type CountdownProgressBar
    @IBOutlet weak var countdownProgressBar: CountdownProgressBar!
    
    @IBOutlet weak var tryAgainLabel: UILabel!
    
    @IBOutlet weak var beginTest: UIButton!
    @IBOutlet weak var epmLabel: UILabel!
    @IBOutlet weak var restartButton: UIButton!
    
    @IBOutlet weak var instructionLabel: UILabel!
    

    @IBOutlet weak var emoji: UILabel!
    @IBOutlet weak var input: UITextField!

    @IBOutlet weak var highScoreLabel: UILabel!
    
 
   @IBAction func restart(_ sender: Any) {
    restarting()
   }
    func restarting(){
        countdownProgressBar.setNeedsDisplay()
        countdownProgressBar.isHidden = true
        //countdownProgressBar.initTimer(duration: 60)
        number = Int.random(in: 0...emojis.count-1)
        print(emojis[number])
        emoji.text = "Type to start: " + emojis[number]
        didBegin = false
        tryAgainLabel.isHidden = true
        count = 0
        let defaults = UserDefaults.standard
        if let stringOne = defaults.string(forKey: defaultsKeys.keyOne) {
            highScoreLabel.text = "High Score: " + stringOne + " EPM"
        }
        epmLabel.isHidden = true
    }
    @IBAction func input(_ sender: Any) {//if user inputs text we call this
        if (didBegin == false){
            startedTyping()
            didBegin = true
            var s = input.text
            s = "Type to start: " + s!
            if (s == emoji.text){
                count += 1
                input.text = ""
                number = Int.random(in: 0...emojis.count-1)
                epmLabel.text = "EPM: " + String(count)
                emoji.text = emojis[number]
                tryAgainLabel.text = "Good job!"
                tryAgainLabel.isHidden = false
            }
            else{
                tryAgainLabel.text = "Try again!"
                epmLabel.text = "EPM: " + String(count)
                input.text = ""
                tryAgainLabel.isHidden = false
                emoji.text = String(emoji.text!.suffix(1))
            }
        }
        else{
            if input.text == emoji.text{
                count += 1
                input.text = ""
                number = Int.random(in: 0...emojis.count-1)
                epmLabel.text = "EPM: " + String(count)
                emoji.text = emojis[number]
                tryAgainLabel.text = "Good job!"
            }
            else{
                epmLabel.text = "EPM: " + String(count)
                input.text = ""
                tryAgainLabel.text = "Try again!"

            }
        }
    }
    @IBAction func begin(_ sender: Any) {
        didBegin = false
        count = 0
        beginTest.isHidden = true
        input.isHidden = false
        emoji.isHidden = false
        restarting()
        
        
    }
    func startedTyping(){
        print("startedTyping")
        countdownProgressBar.isHidden = false
        restartButton.isHidden = false
        epmLabel.isHidden = false
        countdownProgressBar.startCoundown(duration: 60)
        done = true
        instructionLabel.isHidden = true
        
        
    }
    func finished(){
        
        
        if (done){ //the timer continuously calls finished. Did not have time to debug this so used this variable to band-aid the bug
            done = false
            let defaults = UserDefaults.standard
            if let stringOne = defaults.string(forKey: defaultsKeys.keyOne) {
                if (Int(stringOne)! < count) {
                    defaults.set(String(count), forKey: defaultsKeys.keyOne)
                }
            }
            else{
                defaults.set(String(count), forKey: defaultsKeys.keyOne)//if highscore not set. set it
            }
            
            input.isHidden = true
            emoji.isHidden = true
            restartButton.isHidden = true
            beginTest.isHidden = false
            instructionLabel.isHidden = false
            tryAgainLabel.isHidden = true
        }
        
        

        
    }

    override func viewDidLoad() {
        super.viewDidLoad()
        countdownProgressBar.delegate = self
        createGradientView()
        countdownProgressBar.isHidden = true
        restartButton.layer.cornerRadius = 5
        beginTest.layer.cornerRadius = 5
        number = Int.random(in: 0...emojis.count)
        print(emojis[number])
        emoji.text = "Type to begin: " + emojis[number]
        let defaults = UserDefaults.standard
        if let stringOne = defaults.string(forKey: defaultsKeys.keyOne) {
            highScoreLabel.text = "High Score: " + stringOne + " EPM"
        }

    }

    
    
    override func viewDidLayoutSubviews() {
        super.viewDidLayoutSubviews()
        
        
    }
    
    
    
    func createGradientView() {
        
        // overlap the colors and make it 3 sets of colors
        gradientSet.append([colorOne, colorTwo])
        gradientSet.append([colorTwo, colorThree])
        gradientSet.append([colorThree, colorOne])
        
        // set the gradient size to be the entire screen
        gradient.frame = self.view.bounds
        gradient.colors = gradientSet[currentGradient]
        gradient.startPoint = CGPoint(x:0, y:0)
        gradient.endPoint = CGPoint(x:1, y:1)
        gradient.drawsAsynchronously = true
        
        self.view.layer.insertSublayer(gradient, at: 0)// put in the back
        
        animateGradient()
    }
    

    
    func animateGradient() {
        // cycle through all the colors
        if currentGradient < gradientSet.count - 1 {
            currentGradient += 1
        } else {
            currentGradient = 0
        }
        
        // animate over 3 seconds
        let gradientChangeAnimation = CABasicAnimation(keyPath: "colors")
        gradientChangeAnimation.duration = 3.0
        gradientChangeAnimation.toValue = gradientSet[currentGradient]
        gradientChangeAnimation.fillMode = CAMediaTimingFillMode.forwards
        gradientChangeAnimation.isRemovedOnCompletion = false
        gradientChangeAnimation.delegate = self
        gradient.add(gradientChangeAnimation, forKey: "gradientChangeAnimation")
    }
    
    func animationDidStop(_ anim: CAAnimation, finished flag: Bool) {
        
        // restart the animation by changing the color set
        if flag {
            gradient.colors = gradientSet[currentGradient]
            animateGradient()
        }
    }
}
class EmojiTextField: UITextField {

   // required for iOS 13
   override var textInputContextIdentifier: String? { "" } // return non-nil to show the Emoji keyboard

    override var textInputMode: UITextInputMode? {
        for mode in UITextInputMode.activeInputModes {
            if mode.primaryLanguage == "emoji" {
                return mode
            }
        }
        return nil
    }
}
